    return parseXmlResponse(await response.text());
}

async function lookupPatient() {
    try {
        const phoneNumber = msg.payload.phoneNumber;
        const filter = msg.payload.filter;
        const locationGUID = msg.payload.locationGUID;

        if (!phoneNumber && !filter) {
            throw new Error('phoneNumber or filter required');
        }

        // NAME SEARCH: Use GetPortalPatientLookup
        if (filter && !phoneNumber) {
            node.warn('Name search using GetPortalPatientLookup: ' + filter);
            const parsed = await callCloud9('GetPortalPatientLookup', { filter: filter, lookupByPatient: '1', pageIndex: '1', pageSize: '25' });
            const patients = parsed.records.map(p => {
                const firstName = p.PatientFirstName || '';
                const lastName = p.PatientLastName || '';
                const fullName = firstName && lastName ? `${firstName} ${lastName}` : (firstName || lastName || '');
                return {
                    patientGUID: p.PatientGUID || p.patGUID,
                    PatientFirstName: firstName,
                    PatientLastName: lastName,
                    PatientName: fullName,
                    patientName: fullName,
                    PatientID: p.PatientID,
                    PatientBirthDate: p.PatientBirthDate || p.PatientBirthdate || '',
                    birthDate: p.PatientBirthDate || p.PatientBirthdate || '',
                    LocationGUID: p.LocationGUID,
                    LocationName: p.LocationName
                };
            });
            msg.payload = { patients, count: patients.length };
            return msg;
        }

        // PHONE SEARCH: Get patient list, then get contact info for each in parallel batches
        if (phoneNumber) {
            const searchPhone = phoneNumber.replace(/\D/g, '');
            node.warn('Phone search: ' + searchPhone + ' - Getting patient list first');

            const apiParams = {};
            if (locationGUID) apiParams.LocGUIDs = locationGUID;
            const listResult = await callCloud9('GetPatientList', apiParams);

            node.warn('Found ' + listResult.records.length + ' patients, checking phone numbers...');

            // Process in parallel batches for better performance
            const matches = [];
            const batchSize = 10; // Concurrent requests per batch
            const maxToCheck = Math.min(listResult.records.length, 200); // Increased limit for better coverage

            for (let batchStart = 0; batchStart < maxToCheck; batchStart += batchSize) {
                const batchEnd = Math.min(batchStart + batchSize, maxToCheck);
                const batch = listResult.records.slice(batchStart, batchEnd);

                // Process batch in parallel
                const batchPromises = batch.map(async (patient) => {
                    const patGUID = patient.PatientGUID || patient.patGUID;
                    if (!patGUID) return null;

                    try {
                        const infoResult = await callCloud9('GetPatientInformation', { patguid: patGUID });
                        if (infoResult.records.length > 0) {
                            const info = infoResult.records[0];
                            const phones = [
                                info.HomePhone, info.CellPhone, info.WorkPhone,
                                info.Phone, info.PhoneNumber, info.MobilePhone
                            ].filter(Boolean).map(p => p.replace(/\D/g, ''));

                            if (phones.some(p => p.includes(searchPhone) || searchPhone.includes(p.slice(-10)))) {
                                return {
                                    ...patient,
                                    patientGUID: patGUID,
                                    HomePhone: info.HomePhone,
                                    CellPhone: info.CellPhone,
                                    WorkPhone: info.WorkPhone,
                                    Email: info.Email
                                };
                            }
                        }
                    } catch (e) { /* skip on error */ }
                    return null;
                });

                const batchResults = await Promise.all(batchPromises);
                matches.push(...batchResults.filter(Boolean));

                // Stop early if we found matches (optimization for exact phone matches)
                if (matches.length > 0 && searchPhone.length >= 10) break;
            }

            node.warn('Phone search complete. Found ' + matches.length + ' matches (checked ' + maxToCheck + ' patients)');
            msg.payload = { patients: matches, count: matches.length, searchedCount: maxToCheck, totalPatients: listResult.records.length };
            return msg;
        }

        msg.payload = { patients: [], count: 0 };
        return msg;
    } catch (error) {
        node.error('Patient lookup error: ' + error.message, msg);
        msg.payload = { error: 'Failed to lookup patient', message: error.message, timestamp: new Date().toISOString() };
        msg.statusCode = 500;
        return msg;
    }
}

return lookupPatient();

