            birthDate: demographics.PatientBirthDate || '',
            gender: demographics.PatientGender || '',
            email: demographics.PatientEmail || '',
            phone: demographics.PatientPhone || '',
            phoneDescription: demographics.PatientPhoneDescription || '',

