function formatBirthdayForCloud9(dateStr) {
    if (!dateStr) return null;
    const parts = dateStr.split('/');
    if (parts.length !== 3) return dateStr;
    return `${parts[2]}-${parts[0].padStart(2,'0')}-${parts[1].padStart(2,'0')}T00:00:00`;
}

function extractGuidFromResult(result, pattern) {
    if (!result) return null;
    const match = result.match(pattern);
    return match ? match[1] : null;
}

async function createPatient() {
    try {
        const params = msg.payload;
        if (!params.patientFirstName) throw new Error('patientFirstName required');
        if (!params.patientLastName) throw new Error('patientLastName required');

        const providerGUID = params.providerGUID || CLOUD9.defaultProviderGUID;

        // Validate locationGUID is a proper GUID format (UUID), otherwise use default
        // This prevents legacy numeric IDs like '77522' from causing patient creation failures
        let locationGUID = CLOUD9.defaultLocationGUID;
        if (params.locationGUID && /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(params.locationGUID)) {
            locationGUID = params.locationGUID;
        } else if (params.locationGUID) {
            node.warn(`Invalid locationGUID format "${params.locationGUID}", using default: ${CLOUD9.defaultLocationGUID}`);
        }

        node.warn(`Using providerGUID: ${providerGUID}`);
        node.warn(`Using locationGUID: ${locationGUID}`);

        const apiParams = {
            patientFirstName: params.patientFirstName,
            patientLastName: params.patientLastName,
            providerGUID: providerGUID,
            locationGUID: locationGUID,
            VendorUserName: CLOUD9.vendorUserName
        };

        if (params.birthdayDateTime) apiParams.birthdayDateTime = formatBirthdayForCloud9(params.birthdayDateTime);
        if (params.phoneNumber) apiParams.phoneNumber = params.phoneNumber;
        if (params.emailAddress) apiParams.email = params.emailAddress;
        if (params.gender) apiParams.gender = params.gender;

        const xmlRequest = buildXmlRequest('SetPatient', apiParams);
        node.warn('Calling Cloud9 SetPatient');

        const response = await fetch(CLOUD9.endpoint, {
            method: 'POST',
            headers: { 'Content-Type': 'application/xml' },
            body: xmlRequest,
            timeout: 45000
        });

        if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        const xmlText = await response.text();
        const parsed = parseXmlResponse(xmlText);

        const createResult = parsed.records[0]?.Result || '';
        const patientGUID = extractGuidFromResult(createResult, /Patient Added:\s*([A-Fa-f0-9-]+)/i);
        const patientCreated = createResult.includes('Added');

        // LLM Guidance
        const llmGuidance = {
            current_state: patientCreated ? "EXECUTE_BOOKING" : "PATIENT_CREATE_FAILED",
            next_action: patientCreated ? "call_book_child_immediately" : "transfer_to_agent",
            critical_instruction: patientCreated
                ? "Patient created successfully. You MUST now IMMEDIATELY call schedule_appointment_dso with action=book_child using this patientGUID. Do NOT say 'Let me check' or 'One moment'. Do NOT respond to caller until booking is complete."
                : "Patient creation failed. Transfer to live agent.",
            patientGUID_for_booking: patientGUID,
            prohibited_responses: ["Let me check on that", "One moment while I look into this", "I'm verifying", "Let me confirm"],
            booking_sequence: "After patient create -> IMMEDIATELY call book_child -> THEN confirm to caller"
        };

        msg.payload = {
            success: patientCreated,
            patientGUID: patientGUID,
            message: createResult,
            llm_guidance: llmGuidance
        };

        node.warn(`Patient created: ${patientGUID}`);
        return msg;
    } catch (error) {
        node.error('Create patient error: ' + error.message, msg);
        msg.payload = { error: 'Failed to create patient', message: error.message, timestamp: new Date().toISOString() };
        msg.statusCode = 500;
        return msg;
    }
}

return createPatient();

