async function searchSlotsWithExpansion(startDate, endDate, scheduleViewGUIDs) {
    let currentEndDate = endDate;
    let attempt = 0;

    while (attempt < STEPWISE_CONFIG.maxAttempts) {
        attempt++;
        node.warn(`Slot search attempt ${attempt}: ${startDate} to ${currentEndDate}`);

        try {
            const apiParams = {
                startDate: `${startDate} 7:00:00 AM`,
                endDate: `${currentEndDate} 5:00:00 PM`,
                morning: 'True',
                afternoon: 'True',
                appttypGUIDs: CLOUD9.defaultApptTypeGUID
            };
            if (scheduleViewGUIDs) apiParams.schdvwGUIDs = scheduleViewGUIDs;
            const xmlRequest = buildXmlRequest('GetOnlineReservations', apiParams);

            const response = await fetch(CLOUD9.endpoint, {
                method: 'POST',
                headers: { 'Content-Type': 'application/xml' },
                body: xmlRequest,
                timeout: STEPWISE_CONFIG.requestTimeoutMs
            });

            if (!response.ok) throw new Error(`HTTP ${response.status}`);
            const xmlText = await response.text();
            const parsed = parseXmlResponse(xmlText);

            if (parsed.records.length > 0) {
                return {
                    success: true,
                    records: parsed.records,
                    attempts: attempt,
                    expanded: attempt > 1,
                    searchRange: { startDate, endDate: currentEndDate }
                };
            }

            currentEndDate = addDays(currentEndDate, STEPWISE_CONFIG.expansionDays);
        } catch (error) {
            node.error(`Slot search error on attempt ${attempt}: ${error.message}`);
            const isTimeout = ['timeout', 'ETIMEDOUT', 'ECONNRESET'].some(e => error.message.toLowerCase().includes(e.toLowerCase()));
            return {
                success: false,
                records: [],
                attempts: attempt,
                errorType: isTimeout ? 'timeout' : 'api_error',
                shouldTransfer: true
            };
        }
    }

    return { success: false, records: [], attempts: attempt, shouldTransfer: attempt >= STEPWISE_CONFIG.maxAttempts };
}

async function getApptSlots() {
    try {
        const params = msg.payload;
        const dates = validateAndCorrectDates(
            params.startDate || formatDate(new Date(Date.now() + 86400000)),
            params.endDate || formatDate(new Date(Date.now() + 15 * 86400000))
        );

        const result = await searchSlotsWithExpansion(dates.startDate, dates.endDate, params.scheduleViewGUIDs);

        if (result.success && result.records.length > 0) {
            // Inject default appointmentTypeGUID if empty (Cloud9 sandbox returns empty)
            // FIX: Normalize ALL field names to camelCase for LLM consistency
            const DEFAULT_APPT_TYPE_GUID = 'f6c20c35-9abb-47c2-981a-342996016705';
            const enrichedSlots = result.records.map(slot => ({
                ...slot,
