                scheduleViewGUID: slot.ScheduleViewGUID || slot.scheduleViewGUID,
                scheduleColumnGUID: slot.ScheduleColumnGUID || slot.scheduleColumnGUID,
                startTime: slot.StartTime || slot.startTime,
