function groupConsecutiveSlots(slots, numberOfPatients, timeWindowMinutes) {
    const groups = [];
    const sortedSlots = [...slots].sort((a, b) => new Date(a.StartTime) - new Date(b.StartTime));

    for (let i = 0; i <= sortedSlots.length - numberOfPatients; i++) {
        const group = [sortedSlots[i]];
        let lastEndTime = new Date(sortedSlots[i].StartTime);
        lastEndTime.setMinutes(lastEndTime.getMinutes() + parseInt(sortedSlots[i].Minutes || 45));

        for (let j = i + 1; j < sortedSlots.length && group.length < numberOfPatients; j++) {
            const nextStart = new Date(sortedSlots[j].StartTime);
            const timeDiff = (nextStart - lastEndTime) / 60000;

            if (timeDiff >= 0 && timeDiff <= timeWindowMinutes) {
                group.push(sortedSlots[j]);
                lastEndTime = new Date(sortedSlots[j].StartTime);
                lastEndTime.setMinutes(lastEndTime.getMinutes() + parseInt(sortedSlots[j].Minutes || 45));
            }
        }

        if (group.length === numberOfPatients) {
            const date = group[0].StartTime.split(' ')[0];
            groups.push({
                slots: group,
                date: date,
                day: getDayName(date),
                times: group.map(s => s.StartTime.split(' ').slice(1).join(' '))
            });
        }
    }

    return groups;
}

